mapper.update:
update comments
set has_child = #{hasChild}
where comment_id = #{commentId}

update comments
set content = #{content}
where is_deleted = 'N'
  and user_id = #{userId}
  and comment_id = #{commentId}

update comments
set is_deleted = 'Y'
where is_deleted = 'N'
  and user_id = #{userId}
  and comment_id = #{commentId}
