mapper.select:
select count(*)
from comments
where comment_id = #{commentId}
  and user_id = #{userId}
  and is_deleted = 'N'

select c.comment_id, c.parent_id, u.user_id, c.post_id, u.name, c.content, c.created_at as comment_created_at, c.updated_at, i.image_id, i.origin_name, i.stored_name, i.size, i.image_path, i.created_at as image_created_at, (select count(*) from comment_likes a where a.comment_id = c.comment_id) as likes from comments c inner join user u on c.user_id = u.user_id left join comment_image ci on c.comment_id = ci.comment_id left join image i on ci.image_id = i.image_id where c.is_deleted = 'N' and c.post_id = #{postId}
order by c.created_at
order by c.created_at desc

select c.comment_id, c.parent_id, u.user_id, c.post_id, u.name, c.content, c.created_at as comment_created_at, c.updated_at, i.image_id, i.origin_name, i.stored_name, i.size, i.image_path, i.created_at as image_created_at, (select count(*) from comment_likes a where a.comment_id = c.comment_id) as likes, exists (select 1 from comment_likes b where b.comment_id = c.comment_id and b.user_id = #{userId}) as has_like from comments c inner join user u on c.user_id = u.user_id left join comment_image ci on c.comment_id = ci.comment_id left join image i on ci.image_id = i.image_id where c.is_deleted = 'N' and c.post_id = #{postId}
order by c.created_at
order by c.created_at desc

mapper.update:
update comments
set content = #{content}
where is_deleted = 'N'
  and user_id = #{userId}
  and comment_id = #{commentId}

mapper.delete:
delete from comment_image
where comment_id in

    #{commentId}
;
