mapper.select:
select c.comment_id, c.parent_id, u.user_id, c.post_id, u.name, c.content, c.created_at as comment_created_at, c.updated_at, i.image_id, i.origin_name, i.stored_name, i.size, i.image_path, i.created_at as image_created_at, coalesce(lc.likes, 0) as likes from comments c inner join user u on c.user_id = u.user_id left join comment_image ci on c.comment_id = ci.comment_id left join image i on ci.image_id = i.image_id left join (select cl.comment_id, count(cl.like_id) as likes from comment_likes cl group by cl.comment_id) lc on c.comment_id = lc.comment_id where c.is_deleted = 'N' and c.post_id = #{postId}
order by c.created_at
order by c.created_at desc

select c.comment_id, c.parent_id, u.user_id, c.post_id, u.name, c.content, c.created_at as comment_created_at, c.updated_at, i.image_id, i.origin_name, i.stored_name, i.size, i.image_path, i.created_at as image_created_at, coalesce(lc.likes, 0) as likes, coalesce(lc.has_like, 0) as has_like from comments c inner join user u on c.user_id = u.user_id left join comment_image ci on c.comment_id = ci.comment_id left join image i on ci.image_id = i.image_id left join (select cl.comment_id, count(cl.like_id) as likes, max(if(cl.user_id = #{userId}, 1, 0)) as has_like from comment_likes cl group by cl.comment_id) lc on c.comment_id = lc.comment_id where c.is_deleted = 'N' and c.post_id = #{postId}
order by c.created_at
order by c.created_at desc

mapper.update:
update comments
set content = #{content}
where is_deleted = 'N'
  and user_id = #{userId}
  and comment_id = #{commentId}

mapper.delete:
delete from comment_image
where comment_id in

    #{commentId}
;
