mapper.select:
select count(*)
from post_likes
where post_id = #{postId}

select count(*)
from comment_likes
where comment_id = #{commentId}

select count(*)
from post_likes
where user_id = #{userId}
  and post_id = #{postId}

select count(*)
from comment_likes
where user_id = #{userId}
  and comment_id = #{commentId}

mapper.delete:
delete
from post_likes
where user_id = #{userId}
  and post_id = #{postId}

delete
from comment_likes
where user_id = #{userId}
  and comment_id = #{commentId}
