mapper.select:
select count(*)
from posts
where post_id = #{postId}
  and user_id = #{userId}
  and is_deleted = 'N'

mapper.update:
update posts
set title     = #{title},
    content   = #{content},
    has_image = #{hasImage}
where post_id = #{postId}

update posts
set is_deleted = 'Y'
where post_id = #{postId}
